DROP TABLE IF EXISTS `acl_diary`;
CREATE TABLE `acl_diary`  (
  `id` varchar(19) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '主键',
  `title` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '主题',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `content_html` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '内容html存储',
  `visit_count` int(10) NULL DEFAULT 0 COMMENT '访问次数',
  `create_time` datetime(0) NOT NULL COMMENT '创建时间',
  `modified_time` datetime(0) NOT NULL COMMENT '修改时间',
  `is_publish` int(1) NOT NULL DEFAULT 0 COMMENT '是否发布“0”未发布，“1”已发布',
  `is_delete` int(1) NOT NULL DEFAULT 0 COMMENT '逻辑删除“0”未删除，“1”已删除',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

