  `name` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '用户名',
  `target_id` varchar(19) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '管理员评论目标id',
  `content` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `is_check` int(1) NOT NULL DEFAULT 0 COMMENT '管理员是否查看 0未查看 1已查看',
  `create_time` datetime(0) NOT NULL COMMENT '创建时间',
  `is_delete` int(1) NOT NULL DEFAULT 0 COMMENT '逻辑删除 0默认 1已删除',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

